INSERT INTO `complaint` VALUES ('154', '3', 'WY0003', '2013-04-28 00:00:00', '5', '耿磊', '1', '0', null, '邯郸论坛', null, null, '18631012350', '王文超', '当事人耿磊于3月27日9时左右，驾驶助力摩托车行驶至人民路市委门口，被六大队王文超查扣，并给予其罚款400元，记24分的处罚，在缴纳罚款后，对所交停车费表示难以理解，希望有关部门进行解释。', '', '尚局长在支队《收文呈批笺》“建议由督察处核查上报”的拟办意见签署：同意。', '经查，该投诉属实。同时，执勤民警王文超执勤时存在未按规定使用执法记录仪的问题。', '无', '1', '0', '2013-05-02 00:00:00', '2', '1', '1', '该投诉调查完结后，于5月3日上报支队办公室，由支队办公室向市局办公室反馈。', '12', '2013-05-21 10:38:44', '12', '2013-05-21 10:38:44', '');
INSERT INTO `complaint` VALUES ('155', '0', 'TS0010', '2013-05-02 00:00:00', '4', '李中杰 ', '1', '0', '', '永年县', '1', '', '13903206095', '任秋元', '永年县李某带其子投诉永年县交警大队事故科在处理其子李志恒交通事故案件中包庇肇事司机胡进坡酒驾、闯红灯未扣留驾驶员，要求答复。', '', '', '经调查，2011年6月19日李志恒驾驶冀DZX121号小型汽车与河南省胡进坡驾驶豫R59332大型货车发生交通事故。事故发生后，经验血双方司机均未饮酒，因事发路口没有安装监控，无法证明双方是那方闯的红灯，因此法院判决时未采用此证据，事故发生后，只有李志恒一人受伤，对方汽车也未逃逸，不具备对肇事司机采取刑事行政拘留条件。此案已经永年县法院提起民事诉讼，2012年1月5日判决完毕。李中杰反应情况与事实不符，因此答复李中杰此案以法院判决为准。\r\n', '', '2', '0', '2013-05-04 00:00:00', '0', '2', '1', '', '35', '2013-06-13 15:46:27', '35', '2013-06-13 15:46:27', '无异议');
INSERT INTO `complaint` VALUES ('156', '0', 'TS0026', '2013-04-19 00:00:00', '4', '曲德明 ', '1', '0', '', '邯郸市', '2', '', '15544819372', '二大队', '当事人电话投诉，2012年11月8日，驾驶无牌摩托车在长虹桥被交警二大队暂扣，后因家中有事，2013年4月份到二大队接受处理后，停车场告之停车费1500元，当事人曾看到我支队在报纸上刊登过不在收取停车费用，因此要求我单位答复。', '', '政委批示：请顺江同志酌处。', '接到投诉后，经与交警二大队沟通，由二大队代付停车费用，当事人表示满意。', '', '1', '0', '2013-04-22 00:00:00', '2', '1', '1', '', '35', '2013-06-13 15:47:23', '35', '2013-06-13 15:47:23', '无异议');
INSERT INTO `complaint` VALUES ('157', '0', 'TS0027', '2013-04-19 00:00:00', '4', '王富强 ', '1', '0', '', '河沙镇', '2', '', '13315012860', '徐鹏、张延峰', '4月19日，河沙镇王富强来访，投诉南堡防控中队民警在4月14日晚9：30分拦车检查时，将本人冀DU2265号面包车右后车窗砸碎，并将坐在车内后座的当事人妻子右肋骨砸伤，一直未给解决，要求处理。', '', '丁政委批示：请防控大队跟进工作，化解矛盾，解决问题。', '经查，4月14时，南堡防控中队民警张延峰、徐鹏等四人接支队通报，一出租被抢，设卡拦截。王富强驾驶机动车经过时，民警让其停车接受检查时未停，后被民警用警棍将后窗打破，事情发生后，王富强提出20万赔偿，后经防控中队党支部研究决定，民警张延峰等四人写出检查，对当事人赔礼道歉并赔偿相应损失，扣发当月岗位津贴，参加支队执法培训班。对事人提出的20万元赔偿要求不予采纳。', '无', '1', '0', '2013-04-22 00:00:00', '3', '1', '1', '', '35', '2013-06-13 15:50:06', '35', '2013-06-13 15:50:06', '无异议');
INSERT INTO `complaint` VALUES ('158', '0', 'TS0028', '2013-04-18 00:00:00', '4', '杨永广 ', '1', '0', '', '邯郸市', '1', '', '13122003556', '刘玉林', '当事人来访，投诉六大队民警刘玉林，在其人民路临时停车期间与刘玉林发生争吵，刘玉林对其动手，要求管理。', '', '', '我督察民警接到投诉后，将民警刘玉林叫至支队核实，刘玉林承认了本人错误，并向当事人赔礼首次道歉。当事人比较满意。', '', '1', '0', '2013-04-18 00:00:00', '1', '1', '1', '', '35', '2013-06-13 15:50:44', '35', '2013-06-13 15:50:44', '无异议');
INSERT INTO `complaint` VALUES ('159', '0', 'TS0029', '2013-04-17 00:00:00', '4', '张女士 ', '0', '0', '', '邯郸市', '0', '', '13832099754', '康雪川', '张女士电话投诉，4月14，其爱人驾驶冀DD0887号汽车在通过临漳柳园镇时，被当地群众拦截，说其撞人逃逸，后事故科赶到后把其爱人拘留，当事人称，没有撞人，也未逃逸。要求处理。', '', '', '经联系事故民警康雪川，当事司机经过事发地点时，左后轮将一骑自行车行人挂倒，没有停车继续前行，被当地群众拦截。事故发生后，因伤者只是轻伤，民警要求双方调解，但当事司机不同意，要求按正常程序处理，民警将车暂扣后让当事人先回家，后经调取事发地点录像资料和查访周边群众取证，认定汽车与伤者发生了刮擦。后将当事司机叫至事故科正常询问，并未拘禁，是当事人不走。后经事故民警调解，双方达成协议，肇事方赔偿伤者三千元钱。事故处理完结后我督察处又联系投诉人张女士，其表示满意。', '', '2', '0', '2013-04-17 00:00:00', '0', '2', '1', '', '35', '2013-06-13 15:51:03', '35', '2013-06-13 15:51:03', '无异议');
INSERT INTO `complaint` VALUES ('160', '0', 'TS0030', '2013-04-15 00:00:00', '4', '徐俊龙 ', '1', '0', '', '邢台市', '2', '', '13703191020', '朱勇', '4月15日，徐俊龙来信反映，本人驾驶摩托车行至马头工业园区五大队辖区时，被民警以保险造假为由处罚，当时提供了相应证据后，仍被处罚五十元，要求答复。', '', '政委批示：严肃对待，认真查处。', '经调查，当事民警在收到保险公司提供的相应证明后没有采用，存在执法认定过错，而且也没有影象资料，因此建议将此罚单撤消，后当事民警与徐俊龙取得联系，达成谅解，并由民警私人退还五十元钱，后我督察处又对徐先生回访，表示满意。', '', '1', '0', '2013-04-25 00:00:00', '1', '1', '1', '当时执勤执法视频资料丢失。', '35', '2013-06-13 15:51:58', '35', '2013-06-13 15:51:58', '无异议');
INSERT INTO `complaint` VALUES ('161', '0', 'TS0031', '2013-03-11 00:00:00', '4', '刘军 ', '1', '0', '', '峰峰和村', '1', '', '13730052737', '杨海燕', '3月11日，当事人来访，投诉交警二大队车管站民警杨海燕在本人不知情的情况下用假的户籍证明和身份证办理补证业务，造成套证和多起违章，要求答复。', '', '', '经查，二大队李连启负责车管业务期间，利用职务之便，利用当事人身分证复印件违规办理了补证业务。经与二大队协调，撤销了当事人出现套证期间的所有违章。并重新帮其补了新驾驶证。当事人表示满意。', '', '1', '0', '2013-03-14 00:00:00', '0', '1', '1', '二队民警李连启遗留问题。', '35', '2013-06-13 15:52:58', '35', '2013-06-13 15:52:58', '无异议');
INSERT INTO `complaint` VALUES ('162', '0', 'TS0033', '2013-03-05 00:00:00', '3', '', '1', '0', '', '', '2', '', '', '', '群众反映涉县客运市场存在乡材客运车辆驾驶人所持驾驶证与所驾车型不相符的严重情况。', '', '尚局长批示督察处查明情况及时上报', '经查：2013年3月10日、12日、13日，督察民警分别在涉县平涉、索堡、更乐、椿树岭中队，检查过往的乡村客运车辆17辆，未发现所反映的问题。', '存诸电脑D盘', '2', '0', '2013-03-14 00:00:00', '0', '2', '1', '', '6', '2013-06-13 11:36:19', '6', '2013-06-13 11:36:19', '无');
INSERT INTO `complaint` VALUES ('164', '0', 'TS0037', '2013-03-29 00:00:00', '3', '连某 ', '1', '0', '', '', '0', '', '15530057687', '', '反映在曲周大队交纳大型客车非现场处罚时，要求出示驾驶证、行驶证，另外还要求出示机动车所在单位介绍信，认为不合理。', '', '', '经查：2月7日，省厅下发河北省道路交通违法行为\r\n记分管理工作暂行规定第十一条规定大型客运车辆处理非现场处罚的相关规定。已告知当事人。\r\n', '', '2', '0', '2013-03-13 00:00:00', '0', '2', '1', '', '6', '2013-06-13 15:43:49', '6', '2013-06-13 15:43:49', '无');
INSERT INTO `complaint` VALUES ('165', '0', 'TS0036', '2013-03-05 00:00:00', '4', '郝某', '1', '0', '', '邯郸市', '2', '', '18031062380', '市区各大队', '郝某反映市区内许多假冒残疾人专用车，悬挂残疾人专用车牌照，在路上肆意违章，无人管理。', '', '政委批示：由秩序处拟定相关治理方案，下发各大队执行。', '经调查，残疾人专用车车牌是私自制造并安装的，给交通管理带来了极大的不便，后经政委批示，由秩序处拟定相关治理方案，下发各大队执行。', '', '1', '0', '2013-03-08 00:00:00', '0', '1', '1', '', '35', '2013-06-13 15:53:35', '35', '2013-06-13 15:53:35', '无异议');
INSERT INTO `complaint` VALUES ('167', '0', 'TS0043', '2013-04-23 00:00:00', '4', '赵士新', '1', '0', '', '交警六大队', '0', '', '13223136660', '', '赵某车被交警六大队民警查扣，车被按至棉二停车场（冀DGH128、套牌冀DP9951）交纳罚款处理后，被停车场告知要收取300元拖车费及停车费，因支队以前向社会承诺免收上述费用，故投诉。', '', '', '电话通知交警六大队苏队长，19：26当事人赵某来电称事情已解决，车取回未收费。', '', '1', '0', '2013-04-23 00:00:00', '2', '1', '1', '', '11', '2013-06-13 17:06:35', '11', '2013-06-13 17:06:35', '满意');
INSERT INTO `complaint` VALUES ('168', '3', 'WY0014', '2013-04-08 00:00:00', '4', '河北新闻网网民', '1', '0', null, '《河北新闻网》“阳光理政”', null, null, '', '', '2013年4月5日《河北新闻网》“阳光理政”每个到成安交警队去处理违章的驾驶员都遇到了这样的问题，处理违章之前，必须在交警队办理驾驶协会会员和交通金穗卡，强制交60元会费。请问交警队，国家有这样的规定吗？', '', '尚局长批示：请督察、宣传对网上提出的问题进行调查，涉极大队情况查情，并注意网上动态。', '成安大队按照《邯郸市道路交通安全防范责任制规定》（邯郸市人民政府令128号）“机动车驾驶人每年应到少接受一次公安机关交通管理部门或其委托的机构组织的年度交通安全教育培训”的规定，在自愿的前提下，组织驾驶人加入驾协每人收会费30元，并委托驾协对驾驶人开展交通安全教育培训，由本人填写《自愿参加驾协申请书》，持有《培训通知书》并当场开具30元正规票据，农行金穗交通卡是加入驾协时附赔品，不收取任何办卡费用。', '', '2', '0', '2013-04-10 00:00:00', '0', '1', '1', '', '11', '2013-06-13 17:02:51', '11', '2013-06-13 17:02:51', '无法联系上当事人');
INSERT INTO `complaint` VALUES ('169', '3', 'WY0015', '2013-03-28 00:00:00', '4', '河北新闻网网民', '1', '0', null, '《河北新闻网》“阳光理政”版块', null, null, '', '车管所档案管理员曹瑞敏', '2013年3月26日《河北新闻网》“阳光理政”板块反映的车户于2012年2月6日在魏县车管所办理机动车注册登记业务后，其机动车档案没有按时提交到邯郸车管所使当事人无法办理抵押手续。', '', '尚局长批示：请赵支队阅，督察查清情况上报，车管所魏县大队针对出现的问题要向群众说清楚。', '调查结果魏县车管所2013年2月6日机动车注册登记数量比其它月份业务量加大，负责档案管理员曹瑞敏由于疏忽给当事人带来了麻烦，责令档案管理人员曹瑞敏做出书面检查，并按照大队百分制考核规定给予扣分处理。', '', '1', '0', '2013-04-02 00:00:00', '2', '1', '1', '', '11', '2013-06-14 17:06:27', '11', '2013-06-14 17:06:27', '无法联系上当事人');
INSERT INTO `complaint` VALUES ('170', '0', 'TS0047', '2013-03-01 00:00:00', '4', '高怀志 ', '1', '0', '', '魏县', '2', '', '13673102768', '成安车管站', '2月28日，当事人电话投诉，本人去成安交警队车管站审车，外检工作人员称其车轮胎不合格，需更换，后外检人员说拿一百块钱就可以审验合格，当事人和另一名审车的人先后两次给其150元。（附录像）', 'D：赵士坚\\成安车检', '丁政委批示：同意对当事人进行奖励。', '经调取当事人提供的录像资料，当事人投诉接受钱物的民警非大队工作人员，是成安益昌检测中心（民营）工人苏志国，后经我处督促，成安大队做出处理决定：1、益昌检测中心立即停业整整顿。2、开除苏志国。3、车管所当日驻检测中心民警任希朝调离工作岗位。并根据支队对举报人奖励办法，奖励当事人当事人二千元钱，当事人表示满意。', '', '1', '0', '2013-03-03 00:00:00', '3', '1', '1', '', '35', '2013-06-13 15:54:25', '35', '2013-06-13 15:54:25', '无异议');
INSERT INTO `complaint` VALUES ('171', '3', 'WY0016', '2013-03-12 00:00:00', '4', '“起个名字肿么这么难”', '1', '0', null, '新浪微博“起个名字肿么这么难”', null, null, '', '大名大队4050人员', '“起个名字肿么这么难”新浪微博反映：大名交通状况长期混乱？新车上牌照，为什么强制购买灭火器等费用？红绿灯乱，要么就都有用，要么就都没用？规划停车位问题？老县医院路口三个都是红灯问题？贴条三人组向驾驶员要烟问题？', '', '尚局长批示：请涉及到的单位领导应高度重视，采取具体措施，上报整改情况督察处查办，宣传科注意网上动态。', '大名交警队存在五方面的问题：1、2012年5月已取消了强制购买灭火器。2、已对县城13处红绿灯进行了更换维修，安装电子监控设备8处，规范了驾驶员的行车行为。3、县城主要路段重新施划600多个停车位，规范停车秩序。4、已全部进行了更换。5、大队对“4050”三人组严厉批评教育后，调离执法岗位。', '', '1', '0', '2013-03-15 00:00:00', '3', '1', '1', '', '11', '2013-06-14 17:08:30', '11', '2013-06-14 17:08:30', '无法和当事人联系');
INSERT INTO `complaint` VALUES ('172', '3', 'WY0017', '2013-02-24 00:00:00', '4', '网民', '1', '0', null, '新浪微博', null, null, '无', '交警六大队', '2月24日，接市局转来舆情修信息，有网民反映交警六大队五中队在人民路与前进大街附近，对停靠路边的车辆乱贴罚单，望管理。', '', '尚局长批示：请宣传科做好宣传工作，让大家理解了解。', '后经调查，对市局答复如下：交警支队六大队五中队是具有正规执法资格的基层执法中队。在日常执法中负责辖区内违法停车纠正和非现场处罚。五中队执勤民警会根据执法程序依法执法，对乱停乱放车辆进行依法查纠，并全程录像。微博发件人的说法不实，毫无根据。', '', '2', '0', '2013-02-25 00:00:00', '0', '2', '1', '支队宣传科在网上跟贴，回复该网民，无异议。', '35', '2013-06-21 12:00:07', '35', '2013-06-21 12:00:07', '无异议');
INSERT INTO `complaint` VALUES ('173', '3', 'WY0019', '2013-02-20 00:00:00', '4', '网民 666999渎职罪', '1', '0', null, '新浪微博', null, null, '无', '交警六大队', '2月20日接市局转来的《每日网情》，反映网民“666999渎职罪”2月8日在新浪微博称：“人民路和铁西大街为什么封路，并有警车喊话”。', '无', '尚局长批示：请督察处查一下，为什么。', '经询问交警六大队主管勤务副大队长张长青和秩序副处长李鹏，得知2月8日（阴历二十八），市委、市政府相关领导对各企事业单位进行慰问，曾两次通过人民路与铁西大街交叉口，交警六大队并未安排警力封路，秩序处也未安排警车开道，慰问车辆都是按信号灯指示通过路口。后我处又安排专人对网民“666999渎职罪”所发贴子进行跟贴，请其与我支队督察处进行联系，但事后该贴被其本人删除，无法核实该贴的真实性。', '无', '2', '0', '2013-02-25 00:00:00', '0', '2', '1', '', '35', '2013-05-21 11:34:05', '35', '2013-05-21 11:34:05', '无异议');
INSERT INTO `complaint` VALUES ('174', '0', 'TS0050', '2013-01-22 00:00:00', '4', '刘永军 ', '1', '0', '', '邯郸市', '2', '', '13001466281', '许宝山', '1月22日，刘永军来访，2011年4月刘永军与王伟刚发生交通事故，交纳2万元事故压金，扣除相应费用后，剩余6000元事故压金被一大队事故中队民警许宝山扣留至今未还，望我单位进行管理。', '', '尚局长批示：请一队抓紧追回，纪委找本人谈话。', '经查，当事人多次找许宝山催要欠款，许宝山一直没有归还，后经请示支队领导，指示由一大队垫付欠款，并做好当事人安抚工作。后在我督察处的督促下，许宝山归还了欠款。', '', '1', '0', '2013-03-13 00:00:00', '2', '1', '1', '', '35', '2013-06-13 15:55:30', '35', '2013-06-13 15:55:30', '无异议');
INSERT INTO `complaint` VALUES ('175', '0', 'TS0051', '2013-01-17 00:00:00', '4', '马继前 ', '1', '0', '', '邯郸市', '1', '', '13832036161', '王立福', '1月17日，中国新闻报邯郸站记者马继前来访，投诉因未悬挂号牌被交警四大队副大队长王立福处罚，投诉王立福违规执法，要求答复。 ', '', '', '接到投诉后，立即联系四大队副大队长王立福，因马继前是刚上牌照，因无锣丝扣无法安装，在去买扣的过程中被罚，根据支队新出台的便民措施，王立福与其沟通后撤销该罚款，当事人表示满意。', '', '1', '0', '2013-01-17 00:00:00', '2', '1', '1', '', '35', '2013-06-14 15:00:26', '35', '2013-06-14 15:00:26', '无异议');
INSERT INTO `complaint` VALUES ('176', '0', 'TS0052', '2013-01-16 00:00:00', '4', '梁运彬', '1', '0', '', '邯郸市', '2', '', '13932026879', '王志广', '1月15日梁运彬投诉其女儿梁振霞2007年8月22日与张柱才交通肇事逃逸事故存在疑意，要求复查。', '', '政委批示：请何支队并事故处认真做好接待工作。', '经查，梁运彬反映的问题不实，该案已经邯山区法院判决，法院一直未执行，才引起当事人上访，但本着方便群众，事故处决定，由当事民警和法院协商共同出动警力，加大对事故责任方所欠款项的追缴力度。', '', '2', '0', '2013-03-09 00:00:00', '0', '1', '1', '', '35', '2013-06-13 15:57:11', '35', '2013-06-13 15:57:11', '无异议');
INSERT INTO `complaint` VALUES ('177', '0', 'TS0053', '2013-01-14 00:00:00', '4', '李艳春 ', '1', '0', '', '永年县', '0', '', '15831095588', '牛永奇', '永年县冀DB2837号大客车司机李某等联名告永年县交警大队宣传股股长牛永奇，在查扣客运车辆时借机索要好处费，并扰乱县城客运车市场正常经营秩序，要求对牛永奇进行处理。', '', '', '经调查，牛永奇和协勤李场、王晓未承认收取当事人钱物，当事人也没有直接证据证明。但牛永奇对所扣车辆未开据强制措施凭证，因此，根据“五必须、四严禁”和“八项铁规”，规定，经我督察处督促，永年县交警大队研究决定，对牛永奇停止职务，并在全体民警大会上做通报批评。后电话回访当事人李艳春，其表示满意。', '', '1', '0', '2013-03-11 00:00:00', '3', '1', '1', '', '35', '2013-06-13 15:58:20', '35', '2013-06-13 15:58:20', '无异议');
INSERT INTO `complaint` VALUES ('178', '0', 'TS0054', '2013-02-28 00:00:00', '4', '杨培勇 ', '1', '0', '', '邯山区', '2', '', '13223119073', '许宝山', '当事人来访，其在2011年11月份发生一起交通事故，后交给徐宝山事故压金4800元整，事故处理结束后扣除相应费用后，还剩1200元整，至今未返还给当事人，要求处理。', '', '政委批示：请何支队并金海同志查明情况，尽快处理到位。', '经查，当事人多次找许宝山催要欠款，许宝山一直没有归还，后经请示支队领导，指示由一大队垫付欠款，并做好当事人安抚工作。后在我督察处的督促下，许宝山归还了欠款。', '', '1', '0', '2013-03-13 00:00:00', '2', '1', '1', '', '35', '2013-06-13 15:55:03', '35', '2013-06-13 15:55:03', '无异议');
INSERT INTO `complaint` VALUES ('179', '3', 'WY0020', '2013-03-11 00:00:00', '4', '“常军岭”', '1', '0', null, '腾讯微博“常军岭”', null, null, '', '车管所周志杰', '腾讯微博“常军岭”反映：华信车辆登记处周志杰里外勾结上牌的二道贩子，周志杰开着一辆黑色悦达起亚无牌车停到了登记处门口，车贩子上前去问他需要帮忙吗？周志杰说我是这里的头，就说不要在这里干了，赶紧走吧，贩子说那别人在这干就没事吗，周志杰说他办了营业执照的，贩子还有营业执照吗？（信源腾讯微博）', '', '尚局长批示：请督察处上报情况。', '调查结果周志杰是华信机动车登记服务站站长，并没有“常军岭”反映的问题，华信服务站不存在有“三托”及“二道贩子”，冀D6D666号黑色悦达起亚小型车周志杰所驾驶，该车牌在3月初丢失，车管所已对周志杰做出了处罚200元。现车牌已补领安装完毕。', '', '1', '0', '2013-03-14 00:00:00', '2', '1', '1', '', '11', '2013-06-17 15:16:09', '11', '2013-06-17 15:16:09', '联系不上当事人');
INSERT INTO `complaint` VALUES ('180', '3', 'WY0021', '2013-02-27 00:00:00', '4', '咕呼QQ ', '1', '0', null, '新浪博客“消费日报网社会与法频道”的新浪博客于2011年12月12日11：13发表原帖。', null, null, '', '鸡泽交警大队的民警', '咕呼咕呼QQ2211270339”称自己是转发别人的帖子，内容是2011年11月21日韩某冀D84507被鸡泽交警大队曹庄中队民警检查，对韩某车没有过磅检查，就认定我的货车超限30%有异议；2011年11月23日韩某未携带驾驶证，被曹庄中队民警罚款50元有异议；鸡泽县交警大队违规执法敛财、私设小金库。', '', '尚局长批示：请督察牵头对两起舆情召集有关部门尽快解决上报。', '2011年11月21日，曹庄中队民警苏杰、赵伟、尹海涛等执勤中发现一辆冀D84507涉嫌超载要求车辆过地磅做进一步确认，韩某拒绝重新过磅。提供的磅单车货总重为50.6吨,该货车行驶证比对，车核定载质量29吨，车货自重为15吨，该车超出行驶证核定载质量10.6吨，交通法第92条第2款对该车驾驶人做出予以罚款500元的处罚决定。韩某未携带驾驶证，被罚50元违法行为予以撤销，退还韩某50元罚款。大队与鸡泽县农行签订协议，交通违法罚款统一到农行指定的代收点进行缴纳，不存在私设小金库。', '', '1', '0', '2013-03-06 00:00:00', '2', '1', '1', '网宣人员与发帖人网上进行情况核实联系，“咕呼咕呼QQ2211270339”并不是韩某本人，韩某称有鸡泽大队公路三乱视频资料，因无法联系上韩某本人，无从查实。', '11', '2013-06-17 17:03:32', '11', '2013-06-17 17:03:32', '无法联系上当事人韩某');
INSERT INTO `complaint` VALUES ('183', '0', 'TS0057', '2013-01-23 00:00:00', '4', '文江', '1', '0', '记者', '', '0', '', '13831023810', '交警一大队', '邯郸广播电台记者文江称107国道蔚庄批发市场经常有大车占道卖菜。最近几天大雾，高速封路，好多车辆只能从107国道通过，导致蔚庄市场路段堵塞，群众多次反映到清晨热线和交警一大队，希望民警加强车辆管理力度，此情况已有群众反映到央视有关煤体，望支队重视，尽早解决。', '', '丁政委批示：请秩序处协调公交公司查明情况落实立即整改。', '一大队已责成辖区中队增加警力，由五中队中队长张卫东、副中队长程邯友亲自带领6名同志全天不失控、不间断，加大对车辆乱停乱放和疏堵保畅的管控力度。造成该路段堵塞的原因：一是近期雾霾天气的影响造成高速公路封闭致使浴新北大街车流量剧增，二是蔚庄批发市场周边没有停车场，又临近春节，市民自驾车购物增多，车辆停放在道路两侧，三是市容城管部门监管不到位，造成小商小贩随意经营。加强该路段的管理力度，尤其是在雨、雪、雾霾恶劣天气、节假日增加警力，确保该路段道路安全畅通，给广大市民创造一个良好的购物和通行环境。', '', '1', '0', '2013-01-23 00:00:00', '0', '1', '1', '', '11', '2013-06-13 15:55:50', '11', '2013-06-13 15:55:50', '满意');
INSERT INTO `complaint` VALUES ('184', '0', 'TS0060', '2013-01-14 00:00:00', '4', '刘静 ', '0', '0', '', '', '0', '', '13191737806', '民警李建忠', '1月11日下午群众刘某带其子陈某到督察处来访，反映：防控大队二中队中队长李建忠违法违纪，粗暴执法，在民警推拉中致使我胳膊拉伤，至今还有黑青，为了让记者平息事态，在派出所调解下，给了该女子一千元，队长和该女子串通一气声称要敲诈我们一笔钱，以此来解气。作为执法者，他们有无工作证、执法证、上岗证，要求严肃追究其责任。 1月12日，接到《舆情信息》“记者随警采访遭遇野蛮驾驶人殴打批示”后，督察处向中华北防控大队发出督办意见卡，进行督办，并对此事立即展开调查。', '', '尚局长批示：请督察处调查了解维护记者民警合法权益，并报结果。', '刘某反映民警无工作证、执法证、上岗证问题，防控大队二中队中队长李建忠为正式民警，当日带队上路执法，并带有工作证、执法证、上岗证。反映民警粗暴执法，致使刘某胳膊拉伤有黑青痕迹的问题，经调取执法记录仪视频资料和记者提供的相关视频，未发现民警对刘某有推拉现象，无法认定是交警所为。陈某在民警和记者对其驾驶证留存视频证据时，突然抢夺驾驶证，经民警说服教育后将证归还。经派出所调解，刘某给记者李晓燕一千元钱，剩下其它问题还在派出所进一步处理。在查处此起交通违法中，防控大队二中队认定其违法实事准确，对其交通违法行为当事人无异议。关于反映李建忠与记者合伙敲诈当事人的问题，无从查实。', '刘静投诉文件夹', '2', '0', '2013-01-17 00:00:00', '0', '1', '1', '', '11', '2013-06-14 11:25:38', '11', '2013-06-14 11:25:38', '已告知刘某，刘某并不满意。');
INSERT INTO `complaint` VALUES ('185', '3', 'WY0022', '2013-05-13 00:00:00', '4', '“liaochen2013”', '1', '0', null, '每日网情网民“liaochen2013”在《天涯社区》', null, null, '', '巡警', '5月13日9时27分，网民“liaochen2013”在《天涯社区》发表了题为“如此执法，如何服众”的贴文，该贴文无文字叙述，只有一张无牌警车在路上行驶的照片，照片显示该警车后侧4位数字为3175，督察处对事立即展开调查。', '有照片', '尚局长批示：请督察处上报。', '经联系市局勤务保障处张金霞处长，该警车左后侧4位数字为3175属于巡警车辆，并不属于交警支队警车，交警支队警车自编号为1字开头，建议市局督察处督促巡警支队立即改正。', '', '1', '0', '2013-05-15 00:00:00', '0', '1', '1', '', '11', '2013-06-13 17:03:53', '11', '2013-06-13 17:03:53', '无法联系上当事人');
INSERT INTO `complaint` VALUES ('186', '3', 'WY0023', '2013-05-15 00:00:00', '4', '', '1', '0', null, '天涯论坛网', null, null, '', '', '邯郸市临漳交警在处理无证驾驶的违章车辆时，连乘车人员一并罚款1000元。', '', '尚局长批示：请纪委督察调查了解情况，万厅长领导批示尽快落实上报。', '经查4月28日临漳交警大队西羊羔中队民警李耀军、王海英等带队在巡逻过程中发现冀D3V121小型汽车，神情慌张，车速明显减慢。对该车进行检查，司机侯永伟无法出示驾驶证及行车证，副驾驶座乘坐人员杨丙海称“我有证”。杨某持驾驶证C1。根据《道路交通安全法》第99条第一款第一项第一种行为及第二款规定和第99条第一款第二项第一种行为及第二款规定分别对侯某和杨某进行了处罚。民警对杨某、侯某也进行了口头告知，二人当场没有对中队民警执法情况提出异议，并在《公安交通管理行政强制措施凭证上进行了签字》缴纳了罚款。', '临漳大西羊羔投诉文件夹', '2', '0', '2013-05-16 00:00:00', '0', '2', '1', '', '11', '2013-06-14 11:23:22', '11', '2013-06-14 11:23:22', '无法联系上当事人');
INSERT INTO `complaint` VALUES ('187', '0', 'TS0062', '2013-01-14 00:00:00', '4', '刘先生', '1', '0', '', '', '2', '', '13315030506', '法制部门', '投诉人2013年1月9日15时39分反映到市政府督察室、市长公开电话督办室：其冀DRH719的蓝色奔驰车被另一辆黑色奔驰车套了牌，现套牌车辆多次违章，2012年12月20日左右反映到交警二大队，至今没有处理结果，望帮助解决。', '', '市政府督查室、市长公开电话督办室进行联合督办，其中涉及公安机关的督办事项，邯郸市公安局下发交办通知：尚局长批示：请督察阅办。', '经了解，据交警二大队于2012年12月27日受理该案，并确认违法车辆车身颜色与当事人车身颜色不同，车型明显不符。交警二大队于12月28日将《涉嫌套牌机动车协查通知书》分别网上传递给交警一大队、魏县交警大队、指挥中心。但因主办单位与协查单位信息沟通不畅，协查单位未及时开展调查。督察处发出督办卡，责令交警二大队、交警一大队、魏县交警大队和指挥中心认真核实手续及时给予办理，并第一时间向当事人反馈处理情况。要求涉及的四个部门以此为戒，发现问题认真整改，杜绝此类事件发生。\r\n1月16日，督察经查询确定当事人的套牌手续处罚已全部撤销，并告知当事人。\r\n', '', '1', '0', '2013-01-17 00:00:00', '2', '1', '1', '', '9', '2013-06-13 15:49:18', '9', '2013-06-13 15:49:18', '满意');
INSERT INTO `complaint` VALUES ('188', '3', 'WY0024', '2013-05-15 00:00:00', '4', '网民', '1', '0', null, '新浪微博', null, null, '', '', '因婚车发生交通事故，致新娘死亡，交警无人处理。受害人家属在邯郸县政府门前拉横幅维权，并称肇事司机与交警相互勾联，导致无人处理该项事故。', '有图片', '尚局长批示：请纪委督察调查了解情况，尽快落实上报。', '经查3月20程义兵驾驶冀DH3676、挂车号牌冀DRQ19（该车所有人：邯郸市远通运输有限公司）大货车撞上电动自行车孙艳坤和另外两名路人，造成孙艳坤经送医院抢救无效死亡，两名路人受轻微伤，邯郸县事故科民警连夜对此事展开调查提取程义后及受伤人员的血液进行酒精检测。程义兵负此事故的全部责任，孙某及两名路人无责任。当事人及家属均对事故认定无异议。程义兵以无钱为由拒绝调解，对程某刑事拘留，案卷移送邯郸县公安局刑警五中队，县检察院批准对程某进行了逮捕。县法院正在组织对该案开庭审理。邯郸县交警大队事故科民警不存在与废事司机勾联，并不是无人处理该事故。', '', '2', '0', '2013-05-16 00:00:00', '0', '2', '1', '', '11', '2013-06-13 17:09:05', '11', '2013-06-13 17:09:05', '无法和当事人联系');
INSERT INTO `complaint` VALUES ('189', '0', 'TS0063', '2013-01-14 00:00:00', '4', '连先生', '1', '0', '', '', '0', '', '15232804842', '事故民警', '投诉人2013年1月7日10时25分反映到市政府督察室、市长公开电话督办室：2012年10月14日，其叔叔连晨在渚和路车管所路口处发生交通事故后死亡，由邯郸第二交警队事故科受理此案，11月移交到渚和路派出所，但该派出所未处理此案，便不知又将此案移交到何处。', '无', '市政府督查室、市长公开电话督办室进行联合督办，其中涉及公安机关的督办事项，邯郸市公安局下发交办通知：尚局长批示：请督察阅办。', '事故发生后，肇事司机驾车逃逸。交警二大队事故民警连续七个昼日将案件侦破，于10月21日清晨将肇事驾驶员王志锋抓获。下午将犯罪嫌疑人王志锋移交邯山分局渚河路派出所刑事立案侦查，并于 10月25日下达事故认定书，王志锋在此事故中肇事逃逸，承担此事故的全部责任。邯山分局对王志锋以交通肇事罪刑事立案，并于当日将王志锋刑事拘留，经邯山区人民检察院批准，王志锋于10月26日执行逮捕，现被羁押在邯郸市第一看守所内。该案卷现移交邯山区人民检察院处理。民事调解部分如双方最终仍未达成一致意见，应按事故处理程序，当事人向法院申请民事赔偿。', '无', '2', '0', '2013-01-17 00:00:00', '0', '2', '1', '', '9', '2013-06-13 15:57:26', '9', '2013-06-13 15:57:26', '满意');
INSERT INTO `complaint` VALUES ('190', '0', 'TS0065', '2013-01-14 00:00:00', '4', '某先生', '1', '0', '', '', '2', '', '无', '', '某先生2013年1月5日9时22分反映到市政府督察室、市长公开电话督办室：近期，夜间经常有大型吨数较重的货车在南环立交桥上行驶，其认为存在安全隐患。', '', '市政府督查室、市长公开电话督办室进行联合督办，其中涉及公安机关的督办事项，邯郸市公安局下发交办通知：尚局长批示：请督察阅办。', '督察发出督办卡，涉及此情况的相关部门领导经过实地考察：对立交桥分时段、分区域方式进行调查，发现部分路段原有的限高设施已拆除，造成大吨数较重的货车强行通过，确实存在交通安全隐患。交警有关部门积极协调承建施工单位，在桥面全部路段进口处设置永固性限高设施，禁止大货车的通行。\r\n未安装限高设施前，秩序处将协调防控大队、邯郸县大队组织专门警力，在立交桥口加强对大货车的管控力度，最大限度的禁止大货车通行。\r\n', '', '2', '0', '2013-01-15 00:00:00', '0', '2', '1', '', '9', '2013-06-13 16:04:34', '9', '2013-06-13 16:04:34', '无法联系');
INSERT INTO `complaint` VALUES ('191', '0', 'TS0067', '2013-01-14 00:00:00', '4', '赵先生', '1', '0', '', '', '0', '', '13653201102', '交通设施', '投诉人2013年1月3日10时05分反映到市政府督察室、市长公开电话督办室：邯郸市北环路限速40公里，其认为40公里较慢。', '', '市政府督查室、市长公开电话督办室进行联合督办，其中涉及公安机关的督办事项，邯郸市公安局下发交办通知：尚局长批示：请督察阅办。', '督察发出督办卡，设施科经过实地考察：在北环路与中华大街立交桥东口设置限速40公里标志，由北环路立交桥施工单位安装设置，目前，该桥的所有交通设施还未向交警部门移交。交警部门将积极向有关单位建议、协调承建方，及早整改。', '', '2', '0', '2013-01-17 00:00:00', '0', '2', '1', '', '9', '2013-06-17 14:20:35', '9', '2013-06-17 14:20:35', '满意');
INSERT INTO `complaint` VALUES ('192', '0', 'TS0064', '2013-05-11 00:00:00', '4', '邓彦红', '1', '0', '', '', '0', '', '15028457812', '武安交警一中队高洋', '邓某反映2月25日骑摩托车回家，武安市交警一中队民警高洋查车时把车提到了，一下把我腿砸成关节内胫骨平台粉碎性骨折。现做了手术，生活不能自理，医院欠费交警交，生活费不给，武安交警大队给我们谈的赔偿不够我以后治疗费用，拖着不给人身损害赔。邓某要求赔偿60万元，要求你们上级领导帮助解决。', '', '', '2月25日武安交警大队城区域中队民警高洋在塔南岗疏堵时，发现一辆无牌二轮摩托车逆向行驶，导致正常通行车辆无法行驶，民警高洋上前进行拦停询问并要求其驾驶人把车辆停放到不阻碍交通的区域，该驾驶人不听劝阻依旧逆向停放，于是民警高洋准备自己推车，此时该驾驶人强行抢夺摩托车，力气过大导致摩托车侧倒，砸到该驾驶人的妻子邓彦红膝盖上，造成膝盖受伤。事后大队积极对邓彦红进行治疗并提供所有医药费10万元。民警高洋在此次执法过程中未使用执法记录仪，造成无法取证，对此事故的处理造成了一定的难度。大队决定：民警高洋停止工作，写出检查，到最差民警培训班进行教育学习。已告知邓某。', '', '1', '0', null, '3', '1', '12', '', '11', '2013-06-17 13:45:38', '11', '2013-06-17 13:45:38', '无异议');
INSERT INTO `complaint` VALUES ('193', '0', 'TS0068', '2013-02-01 00:00:00', '4', '李海刚', '1', '0', '', '', '2', '', '15232803325', '民警', '投诉人到邯郸交通广播电台反映：其1月4日购买车辆当日被魏县交警大队民警以未悬挂机动车号牌的行为处罚，驾驶证被记满12分。其1月5日缴纳罚款并参加培训，但到现在不能参加考试。其后听说此处罚不合理，要求撤销并退还其所有费用。', '', '丁政委批示：同意督察意见建议各大队引以为戒。', '经联系魏县交警大队违法处理室、12分培训考试负责人，依据对社会公布的“交警支队便民措施”：购车七日内不以未悬挂号牌为由进行处罚的规定。通知魏县交警大队退还投诉人的费用（200元罚款和135元培训费）。关于驾驶证记满12分自动降级，是由于《交通法》修订后违法处理系统自动默认，非人力所为并告知投诉人，其表示理解。', '', '1', '0', '2013-02-05 00:00:00', '2', '1', '1', '', '9', '2013-06-13 15:48:10', '9', '2013-06-13 15:48:10', '满意');
INSERT INTO `complaint` VALUES ('194', '0', 'TS0070', '2013-02-19 00:00:00', '4', '普通市民', '1', '0', '', '', '2', '', '无', '', '投诉人写信反映：邯钢农林生活区有一小混混仗着交警队有人（他自己说），整天开着一辆深灰色无牌照富康车横冲直撞，现在交警查的紧了，按装了一个京P70E25的假牌照，前几天差点撞了人，恳请彻底查办。', '', '尚局长批示：请督察处查处。', '经调取“公安交通管理综合应用平台” 车牌号京P70E25系黄色五菱车辆，可以确定投诉人反映该车属于套牌。督察联系各交警大队，通知每名执勤民警对涉嫌违章车辆京P70E25深灰色富康车进行仔细排查，一经发现立即查扣并进行相应处罚。由于投诉人反映车辆在邯钢生活区，督察怀疑车主为该生活区住户。后了解，邯钢农林生活区系交警二大队辖区。于是和辖区执勤民警分别在20日上午9点、21日早7点30分对整个邯钢农林生活区的每个角落仔细查找（有视频资料），查看该车是否在生活区院内停放。结果均未发现该车。投诉人在信上未留电话号码，所以无法联系沟通以期获得更多的相关信息，目前也没有新的线索。\r\n', '', '2', '0', '2013-02-24 00:00:00', '0', '2', '1', '', '9', '2013-06-13 15:47:25', '9', '2013-06-13 15:47:25', '联系不上');
INSERT INTO `complaint` VALUES ('195', '0', 'TS0071', '2013-02-24 00:00:00', '4', '史会林', '1', '0', '', '成安县辛义乡南徐村3组77号', '2', '130424198409123136', '13722694660', '', '投诉人反映：其本人常年在深圳工作，但驾驶证频繁在外地被套用，违章记录显示有河北原平、武安、衡水等地。从2006年至今，前后违章四十余次。先后找到成安交警大队、邯郸市交警支队，历时一年半均未解决。', '', '申海清批示：请转交警支队督察处，3日内上报调查情况。\r\n尚局长批示：请法制核查。', '督察联系支队法制科办理意见如下：依据现行法律法规的规定，投诉人被套证的办理程序为：1、持相关手续到交通违法行为发生地或当事人所在地的公安机关交通管理部门报案，交通违法行为发生地或当事人所在地的交警大队应当受理。2、如到成安交警大队报案，成安交警大队应当积极开展调查工作，尽快查清案件，给当事人一个满意的结果。成安交警大队在办理异地套证违法时，可积极协调相关交警大队协查，也可商请支队予以协助办理。督察指定成安交警大队法制股股长李纪良负责接待办理，并告知持相关手续到成安交警大队报案，以便套证处罚尽快得到解决，其表示委托亲属携带相关证据去成安交警大队报案。\r\n', '', '2', '0', '2013-02-24 00:00:00', '0', '2', '1', '', '9', '2013-06-13 15:42:47', '9', '2013-06-13 15:42:47', '满意');
INSERT INTO `complaint` VALUES ('199', '0', 'TS0077', '2013-05-22 00:00:00', '4', '李中杰', '1', '0', '', '邯郸市永年县', '2', '', '13931016121', '永年县交警大队', '当事人来访，反映其子在2011年6月发生交通事故，后经法院判决，将对方肇事车辆豫R59332号货车做为事故赔偿一部分赔偿给本人，但去停车场提车时，停车场告之两年停车费为21000余元，根据支队2012年10月19日出台的规定：“被行政执法部门扣车所产生的费用由执法单位承担”，要求我单位给予解决。', '', '政委批示：请闫大队阅处。', '经永年大队与停车场多次协调，全部免除了当事人21000元钱的停车费用，当事人表示满意。', '', '1', '0', '2013-05-25 00:00:00', '0', '1', '1', '', '35', '2013-06-13 15:45:01', '35', '2013-06-13 15:45:01', '满意');
